mapper.select:
  	select * from
  	(
	  	select t1.*,rownum rn from
	  	(
		  	select * from PRIME_CODE_REPORT


			  		and CREATE_TIME >= #{date1}


		  			and CREATE_TIME <= #{date2}


			  		and CTM_BAR_CODE in (${str3})


			  		and STU_NUM in (select stu_num from staff_user where stu_name in (${str4}))


			  		and CTM_NAME like '%${str5}%'


			  		and CTM_TYPE like '%${str6}%'


			  		and LARGE_AREA like '%${str7}%'



		  		order by ${sort} ${order},CREATE_TIME desc


		  		order by CREATE_TIME desc

	  	) t1


	  			rownum <= #{end}


  	)


  			rn > #{start}

  	select SUM(count_ly) c1,COUNT(distinct stu_num) c2 from prime_code_report 


	  	 	 and ctm_type in ${str1}  


	  		and CREATE_TIME >= #{date1}


  			and CREATE_TIME <= #{date2}

  		and count_ly > 0

  	select count(distinct ctm_bar_code) c1,sum(day_count) c2,count(distinct stu_num) c3,sum(count_qsb) c4,sum(count_tjcl) c5,sum(count_sd) c6,sum(count_dd) c7 
  	from prime_code_report


	  	 	 and ctm_type in ${str1}


	  		and CREATE_TIME >= #{date1}


  			and CREATE_TIME <= #{date2}

  	select SUM(count_jd) c1,sum(count_cj) c2,sum(count_gd) c3,sum(count_lpcl) c4,sum(count_kscl) c5,COUNT(distinct stu_num) c6
  	from prime_code_report


	  	 	 and ctm_type in ${str1}


	  		and CREATE_TIME >= #{date1}


  			and CREATE_TIME <= #{date2}

 		and  (count_jd > 0 or count_cj >0 or count_gd>0 or count_lpcl>0 or count_kscl>0)

select stu_num,sum(count_jd) c1,sum(hour_jd) c2,sum(count_cj) c3,sum(hour_cj) c4,sum(count_ly) c5, sum(hour_ly) c6 ,sum(count_gd) c7,sum(hour_gd) c8,sum(count_lpcl) c9,sum(hour_lpcl) c10, sum(count_kscl) c11,sum(hour_kscl) c12,sum(count_qsb) c13,sum(count_qsb) c14, sum(count_tjcl) c15,sum(hour_tjcl) c16,sum(count_sd) c17,sum(count_sd) c18,sum(count_dd) c19,sum(count_dd) c20,sum(day_count) c21 from prime_code_report
and stu_num in (#{str1})
and CREATE_TIME >= #{date1}
and CREATE_TIME <= #{date2}
group by stu_num
