mapper.select:
select 

from REPORT_SIGN
where CTM_BAR_CODE = #{ctmBarCode,jdbcType=VARCHAR}
  and SEND_TIME = #{sendTime,jdbcType=TIMESTAMP}
  and PROVINCE = #{province,jdbcType=VARCHAR}

mapper.delete:
delete from REPORT_SIGN
where CTM_BAR_CODE = #{ctmBarCode,jdbcType=VARCHAR}
  and SEND_TIME = #{sendTime,jdbcType=TIMESTAMP}
  and PROVINCE = #{province,jdbcType=VARCHAR}

mapper.update:
update REPORT_SIGN
set BEGIN_CITY = #{beginCity,jdbcType=VARCHAR},
  CTM_NAME = #{ctmName,jdbcType=VARCHAR},
  COST_HOUR = #{costHour,jdbcType=DECIMAL},
  TOTAL_SEND = #{totalSend,jdbcType=DECIMAL},
  TOTAL_SUCC = #{totalSucc,jdbcType=DECIMAL},
  TOTAL_SUCC_TIMEOUT = #{totalSuccTimeout,jdbcType=DECIMAL},
  WAIT_COURIER_NUMBER = #{waitCourierNumber,jdbcType=VARCHAR}
where CTM_BAR_CODE = #{ctmBarCode,jdbcType=VARCHAR}
  and SEND_TIME = #{sendTime,jdbcType=TIMESTAMP}
  and PROVINCE = #{province,jdbcType=VARCHAR}

mapper.select:
  	select * from
  	(
	  	select t1.*,rownum rn from
	  	(
		  	select * from REPORT_SIGN



		  		order by ${sort} ${order}

	  	) t1


	  			rownum <= #{end}


  	)


  			rn > #{start}

mapper.delete:
delete from REPORT_SIGN
where to_char(SEND_TIME,'yyyy-MM-dd')=#{date}

	and CTM_BAR_CODE=#{ctmBarCode}


	and PROVINCE=#{province}

mapper.select:
select begin_city, ctm_name, ctm_bar_code, province, cost_hour, case when sum(total_send) =0 then 0 else sum(total_succ)/sum(total_send) end as ratioSign, case when sum(total_succ) =0 then 0 else sum(total_succ_timeout)/sum(total_succ) end as ratioTimeout from report_sign where send_time >= #{param1} and send_time < #{param2}
and ctm_bar_code=#{param3,jdbcType=VARCHAR}
and ctm_name=#{param4,jdbcType=VARCHAR}
group by ctm_bar_code,province,cost_hour,ctm_name,begin_city

select begin_city, ctm_name, ctm_bar_code, case when sum(total_send) =0 then 0 else sum(total_succ)/sum(total_send) end as ratioSign, case when sum(total_succ) =0 then 0 else sum(total_succ_timeout)/sum(total_succ) end as ratioTimeout from report_sign where send_time >= #{param1} and send_time < #{param2}
and ctm_bar_code=#{param3,jdbcType=VARCHAR}
and ctm_name=#{param4,jdbcType=VARCHAR}
group by ctm_bar_code,ctm_name,begin_city
