mapper.select:
select * from ( select t1.*,rownum rn from ( select * from SOURCE_IMPORT
and COURIER_NUMBER like '%${str3}%'
and CTM_BAR_CODE like '%${str4}%'
and CTM_NAME like '%${str5}%'
and ORDER_NUMBER like '%${str6}%'
and STU_NUM in (select stu_num from staff_user where stu_name like '%${str7}%')
and (CREATE_TIME >= #{date1} or IS_PUSHED=0)
order by ${sort} ${order},CREATE_TIME desc
order by CREATE_TIME desc
) t1
rownum <= #{end}
)
rn > #{start}

mapper.delete:
 delete from SOURCE_IMPORT
 where CREATE_TIME >= #{date}
	 	  and STU_NUM = #{stuNum}
