mapper.select:
select 

from SOURCE_THIRD_PARTY
where COURIER_NUMBER = #{courierNumber,jdbcType=VARCHAR}
  and RETURN_DATE = #{returnDate,jdbcType=TIMESTAMP}

mapper.delete:
delete from SOURCE_THIRD_PARTY
where COURIER_NUMBER = #{courierNumber,jdbcType=VARCHAR}
  and RETURN_DATE = #{returnDate,jdbcType=TIMESTAMP}

mapper.select:
  	select * from (
		select t1.*,rownum rn from (
		    select s2.* from (select courier_number, max(return_date) tm from SOURCE_THIRD_PARTY group by courier_number) s1,SOURCE_THIRD_PARTY s2

		        s1.courier_number=s2.courier_number and s1.tm=s2.return_date

			  		and s2.CTM_BAR_CODE in (${str1})


