		 			and s2.SEND_TIME <= #{date2}


			  		and s2.DELIVERY_STATE = #{str2}


			  		and s2.COURIER_NUMBER like '%${str3}%'


			  		and s2.CTM_BAR_CODE like '%${str4}%'


			  		and s2.ORDER_NUMBER like '%${str5}%'


			  		and s2.SHOP_NUMBER like '%${str6}%'


			  		and IS_TIME_OUT = #{int1}



		  		order by s2.${sort} ${order},s2.CREATE_TIME desc


		  		order by s2.CREATE_TIME desc

		) t1


	  			rownum <= #{end}


  	)


  			rn > #{start}

  	select count(*) from (select courier_number, max(return_date) tm from SOURCE_THIRD_PARTY group by courier_number) s1,SOURCE_THIRD_PARTY s2

        s1.courier_number=s2.courier_number and s1.tm=s2.return_date

	  		and s2.CTM_BAR_CODE in (${str1})


 			and s2.SEND_TIME >= #{date1}


 			and s2.SEND_TIME <= #{date2}


	  		and s2.DELIVERY_STATE = #{str2}


