mapper.select:
select * from ( select t1.*,rownum rn from ( select from ZM_RETURN_DATA
and SEND_TIME >= #{date1}
and SEND_TIME <= #{date2}
and CTM_BAR_CODE in (${str1})
and CTM_BAR_CODE in (${str2})
and COURIER_NUMBER in (${str3})
and LARGE_AREA like '%${str4}%'
and ORDER_NUMBER like '%${str5}%'
and SLICE_AREA like '%${str7}%'
and FENBU like '%${str8}%'
and FBD_AREA like '%${str9}%'
and PROVINCE like '%${str10}%'
and SHOP_NUMBER like '%${str11}%'
and ADDRESS like '%${str12}%'
and COURIER_STATE = #{int1}
and ( LARGE_AREA is not null or slice_area is not null or fenbu is not null or fbd_area is not null or ctm_bar_code is not null or send_time is not null or weight is not null or province is not null )
order by ${sort} ${order},UPDATE_TIME desc
order by UPDATE_TIME desc
) t1
rownum <= #{end}
)
rn > #{start}

select  from ZM_RETURN_DATA

    and CREATE_DATE >= #{date}
    and COURIER_STATE=0

select a.*,b.hour_cost as cost_hour from (select '深圳' as begin_city, max(ctm_name) as ctm_name, CTM_BAR_CODE, PROVINCE, to_date(to_char(max(SEND_TIME),'yyyy-MM-dd'),'yyyy-MM-dd') as SEND_TIME, count(ctm_bar_code) as total_send from ZM_RETURN_DATA where to_char(SEND_TIME,'yyyy-MM-dd')=#{date,jdbcType=VARCHAR} and province is not null and ctm_bar_code is not null
and ctm_bar_code = #{ctmBarCode}
and province = #{province}
group by CTM_BAR_CODE,PROVINCE)a left join (select * from time_limit) b on a.province=b.end_province

select count(CTM_BAR_CODE) from ZM_RETURN_DATA
where 
to_char(SEND_TIME,'yyyy-MM-dd')=#{0,jdbcType=VARCHAR}
and CTM_BAR_CODE=#{1,jdbcType=VARCHAR}
and ctm_name=#{2,jdbcType=VARCHAR}
and province=#{3,jdbcType=VARCHAR}
and DELIVERY_STATE='配送成功'

select count(a.CTM_BAR_CODE) from ZM_RETURN_DATA a
where 
to_char(a.SEND_TIME,'yyyy-MM-dd')=#{0,jdbcType=VARCHAR}
and a.CTM_BAR_CODE=#{1,jdbcType=VARCHAR}
and a.ctm_name=#{2,jdbcType=VARCHAR}
and a.province=#{3,jdbcType=VARCHAR}
and a.DELIVERY_STATE='配送成功'
and a.sign_time >a.time_out

select count(CTM_BAR_CODE) from ZM_RETURN_DATA
where 
to_char(SEND_TIME,'yyyy-MM-dd')=#{0,jdbcType=VARCHAR}
and CTM_BAR_CODE=#{1,jdbcType=VARCHAR}
and ctm_name=#{2,jdbcType=VARCHAR}
and province=#{3,jdbcType=VARCHAR}
and DELIVERY_STATE in ('配送中','揽件','配送异常')
